queryinfo.query-statement:
SELECT *
FROM
(
    SELECT QNA_SEQ
         , QUEST_TITLE
         , PRJ_SEQ
         , (SELECT PRJ_NM FROM UC_PRJ WHERE PRJ_SEQ=A.PRJ_SEQ) PRJ_NM
         , QUEST_TYPE
         , QUEST_MN_ID
         , DBO.FN_USER(QUEST_MN_ID,'') QUEST_MN_NM
         , PROC_STAT
         , QRY_CNT
         , RPLY_CNT
         , DBO.UF_SYSDATE(REG_DT,'YYYY-MM-DD HH:MI:SS','') REG_DT
    FROM   UC_INF_QNA A
    WHERE  USE_YN='Y'
    @strt_dt @end_dt
    @prj_seq
    @quest_type
    @q_proc_stat
    @quest_title
    @quest_cntn
    @answ_cntn
) B
WHERE 1=1
@quest_mn_nm
ORDER  BY QNA_SEQ DESC
