SELECT '0' AS chk
     , U.user_id     , U.user_pwd   , U.user_nm     , U.hdp_no
     , U.tel_no      , U.home_zipcd , U.home_addr   , U.home_detail_addr
     , U.brth_tc     , U.brth       , U.em_addr     , U.ext_no
     , U.eic_dt      , U.rtrm_dt    , U.wed_mday    , U.atch_file_nm
     , U.use_f       , U.mnu_grp_id , MG.mnu_grp_nm , '0' AS user_chk
     , U.pos_cd      , DBO.FN_CODEBOOK('SYS013', U.pos_cd     ) AS pos_nm
     , U.grd_cd      , DBO.FN_CODEBOOK('SYS007', U.grd_cd     ) AS grd_nm
     , U.nex_dept_cd , DBO.FN_CODEBOOK('SYS025', U.nex_dept_cd) AS nex_dept_nm
     , U.dept_cd     , DBO.FN_CODEBOOK('SYS012', U.dept_cd    ) AS dept_nm
     , U.rg_dt       , U.rg_tm  , U.rg_id  , DBO.FN_USER(U.rg_id , 'Y') AS rg_nm
     , U.mdf_dt      , U.mdf_tm , U.mdf_id , DBO.FN_USER(U.mdf_id, 'Y') AS mdf_nm
     , U.view_org_1  , U.view_org_2, U.view_org_3
FROM   UC_COM_USER U
LEFT   OUTER JOIN UC_COM_MNU_GRP MG ON U.mnu_grp_id = MG.mnu_grp_id
WHERE  1=1
@mnu_grp_id
@grd_cd
@use_f
@q_user_id
@q_user_nm
@view_org_1
@view_org_2
@view_org_3
@tree_view_org_0
@tree_view_org_1
@tree_view_org_2
@tree_view_org_3
ORDER  BY user_id, user_nm

