SELECT a3.wk, a3.s_bse_dt, a3.e_bse_dt, a3.cnt, (a3.cnt - b3.cnt01 - b3.cnt02 - b3.cnt03 - b3.cnt04 - b3.cnt05) AS cnt00, b3.cnt01, b3.cnt02, b3.cnt03, b3.cnt04, b3.cnt05 FROM (
SELECT a2.wk, a2.s_bse_dt, a2.e_bse_dt, COUNT(b2.user_nm) AS cnt FROM ( SELECT wk, MIN(bse_dt) AS s_bse_dt, MAX(bse_dt) AS e_bse_dt FROM ( SELECT bse_dt, DATENAME(wk, CONVERT(datetime, bse_dt, 120)) - (CASE WHEN DATEPART(dw, CONVERT(datetime, bse_dt, 120)) = 1 THEN 1 ELSE 0 END) AS wk FROM uc_com_cld WHERE bse_dt BETWEEN CASE WHEN DATEPART(dw, CONVERT(datetime, ?, 120)) <> 2 THEN DATEADD(d, -1 * (DATEPART(dw, CONVERT(datetime, ?, 120)) -2) , CONVERT(datetime, ?, 120)) ELSE ? END AND ? ) a1 GROUP BY wk ) a2, uc_com_user b2 WHERE b2.eic_dt <= a2.e_bse_dt AND b2.rtrm_dt > a2.e_bse_dt --AND b2.dept_cd IN ('01', '02', '03') AND b2.grd_cd IN ('01') GROUP BY a2.wk, a2.s_bse_dt, a2.e_bse_dt
) a3 LEFT OUTER JOIN (
SELECT a2.wk, a2.s_bse_dt, a2.e_bse_dt, ISNULL(COUNT(CASE WHEN b2.statcd='01' THEN 1 END), 0) AS cnt01, ISNULL(COUNT(CASE WHEN b2.statcd='02' THEN 1 END), 0) AS cnt02, ISNULL(COUNT(CASE WHEN b2.statcd='03' THEN 1 END), 0) AS cnt03, ISNULL(COUNT(CASE WHEN b2.statcd='04' THEN 1 END), 0) AS cnt04, ISNULL(COUNT(CASE WHEN b2.statcd='05' THEN 1 END), 0) AS cnt05 FROM ( SELECT wk, MIN(bse_dt) AS s_bse_dt, MAX(bse_dt) AS e_bse_dt FROM ( SELECT bse_dt, DATENAME(wk, CONVERT(datetime, bse_dt, 120)) - (CASE WHEN DATEPART(dw, CONVERT(datetime, bse_dt, 120)) = 1 THEN 1 ELSE 0 END) AS wk FROM uc_com_cld WHERE bse_dt BETWEEN CASE WHEN DATEPART(dw, CONVERT(datetime, ?, 120)) <> 2 THEN DATEADD(d, -1 * (DATEPART(dw, CONVERT(datetime, ?, 120)) -2) , CONVERT(datetime, ?, 120)) ELSE ? END AND ? ) a1 GROUP BY wk ) a2, uc_wk_rpt b2, uc_com_user c2 WHERE a2.wk = b2.week_gb AND substring(a2.s_bse_dt, 1, 4) = substring(b2.start_dt, 1, 4) AND b2.statcd NOT IN ('06') AND b2.rg_id = c2.user_id --AND c2.dept_cd IN ('01', '02', '03') AND c2.grd_cd IN ('01') GROUP BY a2.wk, a2.s_bse_dt, a2.e_bse_dt
) b3 ON a3.wk = b3.wk
